
$(function(){$(".context-js").contextmenu({target:".context-js-menu"});$(".context-deactivate").contextmenu({target:".context-deactivate-menu",before:function(c,a,b){c.preventDefault();if(c.target.tagName=="CODE"){c.preventDefault();this.closemenu();return false}return true}});$(".context-dynamic").contextmenu({target:".context-dynamic-menu",before:function(a){this.getMenu().find("li").eq(2).find("a").html('<i class="icon-stack"></i> Item has been changed');return true}});$(".context-selection").contextmenu({target:".context-selection-menu",onItem:function(a,b){alert($(b.target).text())}});$(".context-show-menu").on("show.bs.context",function(){alert("onShow event fired")});$(".context-shown-menu").on("shown.bs.context",function(){alert("onShown event fired")});$(".context-hide-menu").on("hide.bs.context",function(){alert("onHide event fired")});$(".context-hidden-menu").on("hidden.bs.context",function(){alert("onHidden event fired")})});