
$(function(){$(".select").select2({width:180,minimumResultsForSearch:Infinity});$(".select").on("change",function(){var a=$(this).val();if((a=="large")){$(".navigation-main").addClass("navigation-lg")}else{$(".navigation-main").removeClass("navigation-lg")}if((a=="small")){$(".navigation-main").addClass("navigation-sm")}else{$(".navigation-main").removeClass("navigation-sm")}if((a=="mini")){$(".navigation-main").addClass("navigation-xs")}else{$(".navigation-main").removeClass("navigation-xs")}})});