
$(function(){Dropzone.autoDiscover=false;$("#dropzone_single").dropzone({paramName:"file",maxFilesize:1,maxFiles:1,dictDefaultMessage:"Drop file to upload <span>or CLICK</span>",autoProcessQueue:false,init:function(){this.on("addedfile",function(a){if(this.fileTracker){this.removeFile(this.fileTracker)}this.fileTracker=a})}});$("#dropzone_multiple").dropzone({paramName:"file",dictDefaultMessage:"Drop files to upload <span>or CLICK</span>",maxFilesize:0.1});$("#dropzone_accepted_files").dropzone({paramName:"file",dictDefaultMessage:"Drop files to upload <span>or CLICK</span>",maxFilesize:1,acceptedFiles:"image/*"});$("#dropzone_remove").dropzone({paramName:"file",dictDefaultMessage:"Drop files to upload <span>or CLICK</span>",maxFilesize:1,addRemoveLinks:true});$("#dropzone_file_limits").dropzone({paramName:"file",dictDefaultMessage:"Drop files to upload <span>or CLICK</span>",maxFilesize:0.4,maxFiles:4,maxThumbnailFilesize:1,addRemoveLinks:true})});